<?php/*	获取系统高级设置字段显示表格*/function getConfigField($pid){	$Model = M("ConfigField");	$where['pid'] = $pid;	$order = "sort ASC,id ASC";    $sub = $Model->where($where)->order($order)->select();    foreach($sub as $sub){    	if($sub['value'] == "false") $check = "checked='checked'";    	else $check = "";		echo "<input type='checkbox' value='{$sub['id']}' name='fields[]' id='{$sub['name']}' $check /><label for='{$sub['name']}'>{$sub['description']}</label>&#12288;";	}}/* 获取 布尔值 下拉列表 */function getBoolSelect($type=0){	if($type == 0){			echo "<li><a href='javascript:void(0)' rel='' name='false'>false</a></li>";			echo "<li><a href='javascript:void(0)' rel='true' name='true'>true</a></li>";	}	elseif($type == 1){			echo "<li><a href='javascript:void(0)' rel='' name='true'>true</a></li>";			echo "<li><a href='javascript:void(0)' rel='false' name='false'>false</a></li>";	}}/* 获取 布尔值  */function getBoolSelectValue($id="",$type=0){	if($type == 0){		if($id == "") echo "false";		else echo "true";	}	elseif($type == 1){		if($id == "") echo "true";		else echo "false";	}}?>