var MSGTIMER=20;var MSGSPEED=5;var MSGOFFSET=3;var MSGHIDE=3;function inlineMsg(G,F,J){var C;var K;if(!document.getElementById("msg")){C=document.createElement("div");C.id="msg";K=document.createElement("div");K.id="msgcontent";document.body.appendChild(C);C.appendChild(K);C.style.filter="alpha(opacity=0)";C.style.opacity=0;C.alpha=0}else{C=document.getElementById("msg");K=document.getElementById("msgcontent")}K.innerHTML=F;C.style.display="block";var D=C.offsetHeight;var I=document.getElementById(G);I.focus();var E=I.offsetHeight;var B=I.offsetWidth;var A=topPosition(I)-((D-E)/2);var H=leftPosition(I)+B+MSGOFFSET;C.style.top=A+"px";C.style.left=H+"px";clearInterval(C.timer);C.timer=setInterval("fadeMsg(1)",MSGTIMER);if(!J){J=MSGHIDE}window.setTimeout("hideMsg()",(J*1000))}function hideMsg(A){var A=document.getElementById("msg");if(!A.timer){A.timer=setInterval("fadeMsg(0)",MSGTIMER)}}function fadeMsg(C){if(C==null){C=1}var A=document.getElementById("msg");var B;if(C==1){B=A.alpha+MSGSPEED}else{B=A.alpha-MSGSPEED}A.alpha=B;A.style.opacity=(B/100);A.style.filter="alpha(opacity="+B+")";if(B>=99){clearInterval(A.timer);A.timer=null}else{if(B<=1){A.style.display="none";clearInterval(A.timer)}}}function leftPosition(B){var A=0;if(B.offsetParent){while(1){A+=B.offsetLeft;if(!B.offsetParent){break}B=B.offsetParent}}else{if(B.x){A+=B.x}}return A}function topPosition(B){var A=0;if(B.offsetParent){while(1){A+=B.offsetTop;if(!B.offsetParent){break}B=B.offsetParent}}else{if(B.y){A+=B.y}}return A}if(document.images){arrow=new Image(7,80);arrow.src="msg_arrow.gif"};